INSERT INTO `originals` VALUES (1,308,NULL,NULL,NULL,NULL,'鰕｛鱼旦｝篇','Креветка и угорь','ЦЧЖ-4',248,'\n<p><span style=\"font-family:SimSun\">鰕</span><span style=\"font-family:SimSun\">｛鱼旦｝游潢潦。</span></p>\n<p><span style=\"font-family:SimSun\">不知江海流。</span></p>\n<p><span style=\"font-family:SimSun\">燕雀戏藩柴。</span></p>\n<p><span style=\"font-family:SimSun\">安识鸿鹄游。</span></p>\n<p><span style=\"font-family:SimSun\">世士此诚明。</span></p>\n<p><span style=\"font-family:SimSun\">大德固无俦。</span></p>\n<p><span style=\"font-family:SimSun\">驾言登五岳。</span></p>\n<p><span style=\"font-family:SimSun\">然后小陵丘。</span></p>\n<p><span style=\"font-family:SimSun\">俯观上路人。</span></p>\n<p><span style=\"font-family:SimSun\">势利惟是谋。</span></p>\n<p><span style=\"font-family:SimSun\">雠高念皇家。</span></p>\n<p><span style=\"font-family:SimSun\">远怀柔九州。</span></p>\n<p><span style=\"font-family:SimSun\">抚剑而雷音。</span></p>\n<p><span style=\"font-family:SimSun\">猛气纵横浮。</span></p>\n<p><span style=\"font-family:SimSun\">泛泊徒嗷嗷。</span></p>\n<p><span style=\"font-family:SimSun\">谁知壮士忧。</span></p>\n',NULL,NULL),(2,308,NULL,NULL,NULL,NULL,'吁嗟篇','Вздохи','ЦЧЖ-1',248,'\n<p><span style=\"font-family:SimSun\">吁嗟此转蓬。</span></p>\n<p><span style=\"font-family:SimSun\">居世何独然。</span></p>\n<p><span style=\"font-family:SimSun\">长去本根逝。</span></p>\n<p><span style=\"font-family:SimSun\">宿夜无休闲。</span></p>\n<p><span style=\"font-family:SimSun\">东西经七陌。</span></p>\n<p><span style=\"font-family:SimSun\">南北越九阡。</span></p>\n<p><span style=\"font-family:SimSun\">卒遇回风起。</span></p>\n<p><span style=\"font-family:SimSun\">吹我入云间。</span></p>\n<p><span style=\"font-family:SimSun\">自谓终天路。</span></p>\n<p><span style=\"font-family:SimSun\">忽然下沉渊。</span></p>\n<p><span style=\"font-family:SimSun\">惊飚接我出。</span></p>\n<p><span style=\"font-family:SimSun\">故归彼中</span><span style=\"font-family:SimSun\">田</span><span style=\"font-family:SimSun\">。</span></p>\n<p><span style=\"font-family:SimSun\">当南而更北。</span></p>\n<p><span style=\"font-family:SimSun\">谓东而反西。</span></p>\n<p><span style=\"font-family:SimSun\">宕宕当何依。</span></p>\n<p><span style=\"font-family:SimSun\">忽亡而复存。</span></p>\n',NULL,NULL),(3,308,NULL,NULL,NULL,NULL,'野田黄雀行','Иволга','ЦЧЖ-5',248,'\n<p><span style=\"font-family:SimSun\">高树多悲风，</span></p>\n<p><span style=\"font-family:SimSun\">海水扬其波。</span></p>\n<p><span style=\"font-family:SimSun\">利剑不在掌，</span></p>\n<p><span style=\"font-family:SimSun\">结友何须多？</span></p>\n<p><span style=\"font-family:SimSun\">不见篱间雀，</span></p>\n<p><span style=\"font-family:SimSun\">见鹞自投罗。</span></p>\n<p><span style=\"font-family:SimSun\">罗家得雀喜，</span></p>\n<p><span style=\"font-family:SimSun\">少年见雀悲。</span></p>\n<p><span style=\"font-family:SimSun\">拔剑捎罗网，</span></p>\n<p><span style=\"font-family:SimSun\">黄雀得飞飞。</span></p>\n<p><span style=\"font-family:SimSun\">飞飞摩苍天，</span></p>\n<p><span style=\"font-family:SimSun\">来下谢少年。</span></p>',NULL,NULL),(4,243,NULL,NULL,NULL,NULL,'栖栖失群鸟','\"Всюду мечется-бьется потерявшая стаю птица …\"','ТЮМ-2',248,'\n<p><span style=\"font-family:SimSun\">栖栖失群鸟，</span></p>\n<p><span style=\"font-family:SimSun\">日暮犹独飞。</span></p>\n<p><span style=\"font-family:SimSun\">徘徊无定止，</span></p>\n<p><span style=\"font-family:SimSun\">夜夜声转悲。</span></p>\n<p><span style=\"font-family:SimSun\">厉响思清远，</span></p>\n<p><span style=\"font-family:SimSun\">去来何依依。</span></p>\n<p><span style=\"font-family:SimSun\">因值孤生松，</span></p>\n<p><span style=\"font-family:SimSun\">敛翮遥来归。</span></p>\n<p><span style=\"font-family:SimSun\">劲风无荣木，</span></p>\n<p><span style=\"font-family:SimSun\">此荫独不衰。</span></p>\n<p><span style=\"font-family:SimSun\">托身已得所，</span></p>\n<p><span style=\"font-family:SimSun\">千载不相违。</span></p>\n',NULL,NULL),(5,243,'饮酒','\"За вином\"',NULL,NULL,'饮酒其五','\"Я поставил свой дом в самой гуще людских жилищ\"','ТЮМ-3',248,'\n<p><span style=\"font-family:SimSun\">结庐在人境，</span></p>\n<p><span style=\"font-family:SimSun\">而无车马喧。</span></p>\n<p><span style=\"font-family:SimSun\">问君何能尔？</span></p>\n<p><span style=\"font-family:SimSun\">心远地自偏。</span></p>\n<p><span style=\"font-family:SimSun\">采菊东篱下，</span></p>\n<p><span style=\"font-family:SimSun\">悠然见南山。</span></p>\n<p><span style=\"font-family:SimSun\">山气日夕佳，</span></p>\n<p><span style=\"font-family:SimSun\">飞鸟相与还。</span></p>\n<p><span style=\"font-family:SimSun\">此中有真意，</span></p>\n<p><span style=\"font-family:SimSun\">欲辨已忘言。</span></p>\n',NULL,NULL),(6,243,'归园田居','\"Возвратился к садам и полям\"',NULL,NULL,'归园田居其一','1. \"С самой юности чужды мне созвучия шумного мира…\")','ТЮМ-4',248,'<p><span style=\"font-family: SimSun;\">少无适俗韵，</span></p>\r\n<p><span style=\"font-family: SimSun;\">性本爱丘山。</span></p>\r\n<p><span style=\"font-family: SimSun;\">误落尘网中，</span></p>\r\n<p><span style=\"font-family: SimSun;\">一去十三年。</span></p>\r\n<p><span style=\"font-family: SimSun;\">羁鸟恋旧林，</span></p>\r\n<p><span style=\"font-family: SimSun;\">池鱼思故渊。</span></p>\r\n<p><span style=\"font-family: SimSun;\">开荒南野际，</span></p>\r\n<p><span style=\"font-family: SimSun;\">抱拙归园</span><span style=\"font-family: SimSun;\">田</span><span style=\"font-family: SimSun;\">。</span></p>\r\n<p><span style=\"font-family: SimSun;\">方宅十像亩，</span></p>\r\n<p><span style=\"font-family: SimSun;\">草屋八九间。</span></p>\r\n<p><span style=\"font-family: SimSun;\">榆柳荫後檐，</span></p>\r\n<p><span style=\"font-family: SimSun;\">桃李罗堂前。</span></p>\r\n<p>82<span style=\"font-family: SimSun;\">暧暧远人村</span>,</p>\r\n<p><span style=\"font-family: SimSun;\">依依墟里烟。</span></p>\r\n<p><span style=\"font-family: SimSun;\">狗吠深巷中，</span></p>\r\n<p><span style=\"font-family: SimSun;\">鸡鸣桑树颠。</span></p>\r\n<p><span style=\"font-family: SimSun;\">户庭无尘杂，</span></p>\r\n<p><span style=\"font-family: SimSun;\">虚室有馀闲。</span></p>\r\n<p><span style=\"font-family: SimSun;\">久在樊笼里，</span></p>\r\n<p><span style=\"font-family: SimSun;\">复得返自然。</span></p>',NULL,NULL),(7,243,'归园田居','\"Возвратился к садам и полям\"',NULL,NULL,'归园田居其二','2. \"Здесь, в глуши деревенской, дел мирских человеческих мало…\"','ТЮМ-5',248,'\n<p><span style=\"font-family:SimSun\">野外罕人事，</span></p>\n<p><span style=\"font-family:SimSun\">穷巷寡轮鞅。</span></p>\n<p><span style=\"font-family:SimSun\">白日掩荆扉，</span></p>\n<p><span style=\"font-family:SimSun\">虚室绝尘想。</span></p>\n<p><span style=\"font-family:SimSun\">时复墟曲中，</span></p>\n<p><span style=\"font-family:SimSun\">披草共来往。</span></p>\n<p><span style=\"font-family:SimSun\">相见无杂言，</span></p>\n<p><span style=\"font-family:SimSun\">但道桑麻长。</span></p>\n<p><span style=\"font-family:SimSun\">桑麻日己长，</span></p>\n<p><span style=\"font-family:SimSun\">我土日己广。</span></p>\n<p><span style=\"font-family:SimSun\">常恐霜霰至，</span></p>\n<p><span style=\"font-family:SimSun\">零落同草莽。</span></p>\n',NULL,NULL),(8,243,'归园田居','\"Возвратился к садам и полям\"',NULL,NULL,'归园田居其三','3. \"Вот бобы посадил я на участке под Южной горою …\"','ТЮМ-6',248,'\n<p><span style=\"font-family:SimSun\">种豆南山下</span>,</p>\n<p><span style=\"font-family:SimSun\">草盛豆苗稀。</span></p>\n<p><span style=\"font-family:SimSun\">晨兴理荒秽，</span></p>\n<p><span style=\"font-family:SimSun\">带月荷锄归。</span></p>\n<p><span style=\"font-family:SimSun\">道狭草木长，</span></p>\n<p><span style=\"font-family:SimSun\">夕露沾我衣。</span></p>\n<p><span style=\"font-family:SimSun\">衣沾不足惜，</span></p>\n<p><span style=\"font-family:SimSun\">但使愿无违。</span></p>\n',NULL,NULL),(9,243,'归园田居','\"Возвратился к садам и полям\"',NULL,NULL,'归园田居其四','4. \"С давних пор так бывало - ухожу я и в горы и к рекам …\"','ТЮМ-7',248,'\n<p><span style=\"font-family:SimSun\">久去山泽游，</span></p>\n<p><span style=\"font-family:SimSun\">浪莽林野娱。</span></p>\n<p><span style=\"font-family:SimSun\">试携子侄辈，</span></p>\n<p><span style=\"font-family:SimSun\">披榛步荒墟。</span></p>\n<p><span style=\"font-family:SimSun\">徘徊丘陇间，</span></p>\n<p><span style=\"font-family:SimSun\">依依昔人居。</span></p>\n<p><span style=\"font-family:SimSun\">井灶有遗处，</span></p>\n<p><span style=\"font-family:SimSun\">桑竹残朽株</span></p>\n<p><span style=\"font-family:SimSun\">借问采薪者，</span></p>\n<p><span style=\"font-family:SimSun\">此人皆焉如？</span></p>\n<p><span style=\"font-family:SimSun\">薪者向我言：</span></p>\n<p>“<span style=\"font-family:SimSun\">死殁无复馀</span>”<span style=\"font-family:SimSun\">。</span></p>\n<p>“<span style=\"font-family:SimSun\">一世异朝市</span>”<span style=\"font-family:SimSun\">，</span></p>\n<p><span style=\"font-family:SimSun\">此语真不虚！</span></p>\n<p><span style=\"font-family:SimSun\">人生似幻化，</span></p>\n<p><span style=\"font-family:SimSun\">终当归空无。</span></p>\n',NULL,NULL),(10,243,'归园田居','\"Возвратился к садам и полям\"',NULL,NULL,'归园田居其五','5. \"Никого. И в печали я иду, опираясь на палку…\"','ТЮМ-8',248,'\n<p><span style=\"font-family:SimSun\">怅恨独策还，</span></p>\n<p><span style=\"font-family:SimSun\">崎岖历榛曲。</span></p>\n<p><span style=\"font-family:SimSun\">山涧清且浅，</span></p>\n<p><span style=\"font-family:SimSun\">遇以濯吾足。</span></p>\n<p><span style=\"font-family:SimSun\">漉我新熟酒，</span></p>\n<p><span style=\"font-family:SimSun\">双鸡招近局。</span></p>\n<p><span style=\"font-family:SimSun\">日入室中暗，</span></p>\n<p><span style=\"font-family:SimSun\">荆薪代明烛。</span></p>\n<p><span style=\"font-family:SimSun\">欢来苦夕短，</span></p>\n<p><span style=\"font-family:SimSun\">已复至天旭。</span></p>',NULL,NULL),(11,123,NULL,NULL,NULL,NULL,'咏鹅','Га-га-га','ЛБВ-1',321,'<p><span style=\"font-family: SimSun;\">鹅，鹅，鹅，</span></p>\r\n<p><span style=\"font-family: SimSun;\">曲项向天歌。</span></p>\r\n<p><span style=\"font-family: SimSun;\">白毛浮绿水，</span></p>\r\n<p><span style=\"font-family: SimSun;\">红掌拨清波。</span></p>',NULL,NULL),(12,14,NULL,NULL,NULL,NULL,'次北固山下','Доезжаю до подножия горы Бэйгушань','ВВН-1',321,'<p><span style=\"font-family: SimSun;\">客路青山外</span></p>\r\n<p><span style=\"font-family: SimSun;\">行舟綠水前</span></p>\r\n<p><span style=\"font-family: SimSun;\">潮平兩岸闊</span></p>\r\n<p><span style=\"font-family: SimSun;\">風正一帆懸</span></p>\r\n<p><span style=\"font-family: SimSun;\">海日生殘夜</span></p>\r\n<p><span style=\"font-family: SimSun;\">江春入舊年</span></p>\r\n<p><span style=\"font-family: SimSun;\">鄉書何處達</span></p>\r\n<p><span style=\"font-family: SimSun;\">歸雁洛陽邊</span></p>',NULL,NULL),(13,14,NULL,NULL,NULL,NULL,'次北固山下','Под северной горой','ВВН-1',NULL,'<p><span style=\"font-family: \'SimSun\';\">客路青山外，</span></p>\r\n<p><span style=\"font-family: SimSun;\">行舟绿水前。</span></p>\r\n<p><span style=\"font-family: SimSun;\">潮平两岸阔，</span></p>\r\n<p><span style=\"font-family: SimSun;\">风正一帆悬。</span></p>\r\n<p><span style=\"font-family: SimSun;\">海日生残夜，</span></p>\r\n<p><span style=\"font-family: SimSun;\">江春入旧年。</span></p>\r\n<p><span style=\"font-family: SimSun;\">乡书何处达？</span></p>\r\n<p><span style=\"font-family: SimSun;\">归雁洛阳边。</span></p>\r\n<div class=\"d-flex align-items-end\"><cite title=\"неизвестен\"><a id=\"biblio_poems\" href=\"https://www.shicihui.com/mingju/2276.html\">Источник: сайт https://www.shicihui.com/mingju/2276.html</a></cite></div>',NULL,NULL),(130,35,NULL,NULL,NULL,NULL,'登鶴鵲樓','Поднимаюсь на башню аиста','ВЧХ-1',335,'<p>白日依山盡，</p>\r\n<p>黃河入海流。</p>\r\n<p>欲窮千里目，</p>\r\n<p>更上一層樓。</p>','Ши, стихотворная форма цзиньтиши','Цзюэцзюй (усеченные строфы) пятисловное четверостишие (уяньши)'),(14,102,NULL,NULL,NULL,NULL,'静夜思','Думы в тихую ночь','ЛБ-1',248,'\n<p><span style=\"font-family:SimSun\">床前明月光，</span></p>\n<p><span style=\"font-family:SimSun\">疑是地上霜。</span></p>\n<p><span style=\"font-family:SimSun\">举头望明月，</span></p>\n<p><span style=\"font-family:SimSun\">低头思故乡。</span></p>\n',NULL,NULL),(15,102,NULL,NULL,NULL,NULL,'春思','Весенние думы','ЛБ-8',248,'\n<p><span style=\"font-family:SimSun\">燕草如碧丝</span>,</p>\n<p><span style=\"font-family:SimSun\">秦桑低绿枝。</span></p>\n<p><span style=\"font-family:SimSun\">当君怀归日，</span></p>\n<p><span style=\"font-family:SimSun\">是妾断肠时。</span></p>\n<p><span style=\"font-family:SimSun\">春风不相识，</span></p>\n<p><span style=\"font-family:SimSun\">何事入罗帏。</span></p>\n',NULL,NULL),(16,102,NULL,NULL,NULL,NULL,'独坐敬亭山','Одиноко сижу в горах Цзинтиншань','ЛБ-2',248,'\n<p><span style=\"font-family:SimSun\">众鸟髙飞尽，</span></p>\n<p><span style=\"font-family:SimSun\">孤云独去闲。</span></p>\n<p><span style=\"font-family:SimSun\">相看两不厌，</span></p>\n<p><span style=\"font-family:SimSun\">只有敬亭山</span></p>\n',NULL,NULL),(17,102,NULL,NULL,NULL,NULL,'哭宣城善酿纪叟','Оплакиваю славного сюаньчэнского винодела, старика','ЛБ-9',248,'\n<p><span style=\"font-family:SimSun\">纪叟黄泉里，</span></p>\n<p><span style=\"font-family:SimSun\">还应酿老春。</span></p>\n<p><span style=\"font-family:SimSun\">夜台无晓日，</span></p>\n<p><span style=\"font-family:SimSun\">沾酒与何人。</span></p>\n',NULL,NULL),(18,102,NULL,NULL,NULL,NULL,'访戴天山道士不遇','Навещаю отшельника на горе Дайтянь, но не застаю его','ЛБ-30',248,'\n<p><span style=\"font-family:SimSun\">犬吠水声中，</span></p>\n<p><span style=\"font-family:SimSun\">桃花带雨浓。</span></p>\n<p><span style=\"font-family:SimSun\">树深时见鹿，</span></p>\n<p><span style=\"font-family:SimSun\">溪午不闻钟。</span></p>\n<p><span style=\"font-family:SimSun\">野竹分青霭，</span></p>\n<p><span style=\"font-family:SimSun\">飞泉挂碧峰。</span></p>\n<p><span style=\"font-family:SimSun\">无人知所去，</span></p>\n<p><span style=\"font-family:SimSun\">愁倚两三松。</span></p>\n',NULL,NULL),(19,102,NULL,NULL,NULL,NULL,'白鹭鸶','Вижу белую цаплю','ЛБ-10',248,'\n<p><span style=\"font-family:SimSun\">白鹭下秋水，</span></p>\n<p><span style=\"font-family:SimSun\">孤飞如坠霜。</span></p>\n<p><span style=\"font-family:SimSun\">心闲且未去，</span></p>\n<p><span style=\"font-family:SimSun\">独立沙洲傍</span></p>\n',NULL,NULL),(20,102,NULL,NULL,NULL,NULL,'把酒问月','С кубком в руке вопрошая луну','ЛБ-16',248,'\n<p><span style=\"font-family:SimSun\">青天有月来几时，</span></p>\n<p><span style=\"font-family:SimSun\">我今停杯一问之：</span></p>\n<p><span style=\"font-family:SimSun\">人攀明月不可得，</span></p>\n<p><span style=\"font-family:SimSun\">月行却与人相随？</span></p>\n<p><span style=\"font-family:SimSun\">皎如飞镜临丹阙，</span></p>\n<p><span style=\"font-family:SimSun\">绿烟灭尽清辉发？</span></p>\n<p><span style=\"font-family:SimSun\">但见宵从海上来，</span></p>\n<p><span style=\"font-family:SimSun\">宁知晓向云间没？</span></p>\n<p><span style=\"font-family:SimSun\">白兔捣药秋复春，</span></p>\n<p><span style=\"font-family:SimSun\">嫦娥孤栖与谁邻？</span></p>\n<p><span style=\"font-family:SimSun\">今人不见古时月，</span></p>\n<p><span style=\"font-family:SimSun\">今月曾经照古人。</span></p>\n<p><span style=\"font-family:SimSun\">古人今人若流水，</span></p>\n<p><span style=\"font-family:SimSun\">共看明月皆如此。</span></p>\n<p><span style=\"font-family:SimSun\">唯愿当歌对酒时，</span></p>\n<p><span style=\"font-family:SimSun\">月光长照金樽里。</span></p>\n',NULL,NULL),(21,102,NULL,NULL,NULL,NULL,'早发白帝城','Рано утром выезжаю из города Боди','ЛБ-17',248,'\n<p><span style=\"font-family:SimSun\">朝辞白帝彩云间，</span></p>\n<p><span style=\"font-family:SimSun\">千里江陵一日还。</span></p>\n<p><span style=\"font-family:SimSun\">两岸猿声啼不尽，</span></p>\n<p><span style=\"font-family:SimSun\">轻舟已过万重山。</span></p>\n',NULL,NULL),(22,102,NULL,NULL,NULL,NULL,'望木瓜山','Глядя на гору Айвы','ЛБ-11',248,'\n<p><span style=\"font-family:SimSun\">早起见日出，</span></p>\n<p><span style=\"font-family:SimSun\">暮见栖鸟还。</span></p>\n<p><span style=\"font-family:SimSun\">客心自酸楚，</span></p>\n<p><span style=\"font-family:SimSun\">况对木瓜山。</span></p>\n',NULL,NULL),(23,102,NULL,NULL,NULL,NULL,'戏赠杜甫','Шутя преподношу моему другу Ду Фу','ЛБ-12',248,'\n<p><span style=\"font-family:SimSun\">饭颗山头逢杜甫，</span></p>\n<p><span style=\"font-family:SimSun\">顶戴笠子日卓午。</span></p>\n<p><span style=\"font-family:SimSun\">借问别来太瘦生，</span></p>\n<p><span style=\"font-family:SimSun\">总为从前作诗苦。</span></p>\n',NULL,NULL),(24,102,NULL,NULL,NULL,NULL,'客中行','Среди чужих','ЛБ-18',248,'\n<p><span style=\"font-family:SimSun\">兰陵美酒郁金香，</span></p>\n<p><span style=\"font-family:SimSun\">玉碗盛来琥珀光。</span></p>\n<p><span style=\"font-family:SimSun\">但使主人能醉客，</span></p>\n<p><span style=\"font-family:SimSun\">不知何处是他乡</span>О</p>\n',NULL,NULL),(25,102,NULL,NULL,NULL,NULL,'柴诗','Без названия','ЛБ-19',248,'\n<p><span style=\"font-family:SimSun\">白日与明月，</span></p>\n<p><span style=\"font-family:SimSun\">昼夜尚不闲。</span></p>\n<p><span style=\"font-family:SimSun\">况尔悠悠人，</span></p>\n<p><span style=\"font-family:SimSun\">安得久世间。</span></p>\n<p><s